using Terraria;using Terraria.ModLoader;namespace ExxoAvalonOrigins.Buffs.AdvancedBuffs{	public class AdvFury : ModBuff	{		public override void SetDefaults()		{			DisplayName.SetDefault("Advanced Fury");			Description.SetDefault("60% increased critical strike damage");		}		public override void Update(Player player, ref int k)		{			player.GetModPlayer<ExxoAvalonOriginsModPlayer>().critDamageMult += 0.60f;		}	}}