using Terraria;using Terraria.ModLoader;namespace ExxoAvalonOrigins.Buffs{	public class Fury : ModBuff	{		public override void SetDefaults()		{			DisplayName.SetDefault("Fury");			Description.SetDefault("30% increased critical strike damage");		}		public override void Update(Player player, ref int k)		{			player.GetModPlayer<ExxoAvalonOriginsModPlayer>().critDamageMult += 0.30f;		}	}}