using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class AIController : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("AI Controller");			Tooltip.SetDefault("A stinger probe minion circles you"				+ "\nThis probe reflects projectiles and fires stingers at enemies");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/AIController");			item.rare = -12;			item.width = dims.Width;			item.accessory = true;			item.value = Item.sellPrice(0, 15, 0, 0);			item.height = dims.Height;			item.expert = true;		}		public override void UpdateAccessory(Player player, bool hideVisual)		{			player.GetModPlayer<ExxoAvalonOriginsModPlayer>().stingerProbeMinion = true;		}	}}