using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class AdventuresandMishaps : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Adventures and Mishaps");			Tooltip.SetDefault("+60 HP, +5% damage\n-10% mana cost");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/AdventuresandMishaps");			item.rare = 7;			item.width = dims.Width;			item.value = 20000;			item.height = dims.Height;            item.GetGlobalItem<ExxoAvalonOriginsGlobalItemInstance>().tome = true;        }        public override void UpdateAccessory(Player player, bool hideVisual)        {            player.statLifeMax2 += 60;            player.magicDamage += 0.05f;            player.minionDamage += 0.05f;            player.meleeDamage += 0.05f;            player.rangedDamage += 0.05f;            player.thrownDamage += 0.05f;            player.manaCost -= 0.1f;        }    }}