using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	[AutoloadEquip(EquipType.Head)]	class AncientHeadpiece : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Ancient Headpiece");			Tooltip.SetDefault("20% increased damage\n5% increased critical strike chance");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/AncientHeadpiece");			item.defense = 30;			item.rare = 9;			item.width = dims.Width;			item.value = Item.sellPrice(0, 50, 0, 0);			item.height = dims.Height;		}		public override bool IsArmorSet(Item head, Item body, Item legs)		{			return body.type == ModContent.ItemType<AncientBodyplate>() && legs.type == ModContent.ItemType<AncientLeggings>();		}		public override void UpdateArmorSet(Player player)		{			player.setBonus = "Attackers also take full damage, Spectre Heal, and not moving puts you in stealth";			player.ghostHeal = true;			//player.thorns = true;			player.turtleThorns = true;			player.shroomiteStealth = true;		}		public override void UpdateEquip(Player player)		{			player.rangedDamage += 0.2f;			player.meleeDamage += 0.2f;			player.minionDamage += 0.2f;			player.magicDamage += 0.2f;			player.magicCrit += 5;			player.meleeCrit += 5;			player.rangedCrit += 5;		}	}}