using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	[AutoloadEquip(EquipType.Body)]	class AwakenedRosePlateMail : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Awakened Rose Plate Mail");			Tooltip.SetDefault("Spectre Heal and Spectre Damage");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/AwakenedRosePlateMail");			item.defense = 21;			item.rare = 10;			item.width = dims.Width;			item.value = Item.sellPrice(0, 40, 0, 0);			item.height = dims.Height;		}		public override void UpdateEquip(Player player)		{			player.ghostHeal = true;			player.ghostHurt = true;			player.GetModPlayer<ExxoAvalonOriginsModPlayer>().ghostSilence = true;		}	}}