using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class BombSkeletonBanner : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Bomb Skeleton Banner");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/BombSkeletonBanner");			item.autoReuse = true;			item.useTurn = true;			item.maxStack = 999;			item.consumable = true;			item.createTile = TileID.Banners;			item.placeStyle = 176;			item.rare = 1;			item.width = dims.Width;			item.useTime = 10;			item.useStyle = 1;			item.value = 1000;			item.useAnimation = 15;			item.height = dims.Height;		}	}}