using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class BronzeBow : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Bronze Bow");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/BronzeBow");			item.UseSound = SoundID.Item5;			item.damage = 7;			item.useTurn = true;			item.scale = 1f;			item.shootSpeed = 6f;			item.useAmmo = AmmoID.Arrow;			item.ranged = item.noMelee = true;			item.width = dims.Width;			item.useTime = 29;			item.knockBack = 0f;			item.shoot = 1;			item.useStyle = 5;
            item.value = 1500;			item.useAnimation = 29;			item.height = dims.Height;		}	}}