using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class BurningDesire : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Burning Desire");			Tooltip.SetDefault("Tome\n+40 HP\n+40 mana");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/BurningDesire");			item.rare = ItemRarityID.Orange;			item.width = dims.Width;			item.value = 15000;			item.height = dims.Height;            item.GetGlobalItem<ExxoAvalonOriginsGlobalItemInstance>().tome = true;		}        public override void UpdateAccessory(Player player, bool hideVisual)        {            player.statLifeMax2 += 40;            player.statManaMax2 += 40;        }    }}