using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class CoughwoodWorkBench : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Coughwood Work Bench");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/CoughwoodWorkBench");			item.autoReuse = true;			item.consumable = true;			item.createTile = TileID.WorkBenches;			item.placeStyle = 28;			item.width = dims.Width;			item.useTurn = true;			item.useTime = 10;			item.scale = 1f;			item.useStyle = 1;			item.maxStack = 99;			item.useAnimation = 15;			item.height = dims.Height;		}	}}