using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class DurataniumShield : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Duratanium Shield");			Tooltip.SetDefault("Slows the effects of damage over time debuffs");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/DurataniumShield");			item.defense = 2;			item.rare = 4;			item.width = dims.Width;			item.value = 54000;			item.accessory = true;			item.height = dims.Height;		}		public override void UpdateAccessory(Player player, bool hideVisual)		{			player.GetModPlayer<ExxoAvalonOriginsModPlayer>().frontReflect = true;		}	}}