using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class Emperor : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Emperor");			Tooltip.SetDefault("+25% damage, +12% critical strike chance, -20% mana cost\n30% chance to not consume ammo, 14 defense, +200 mana, +100 HP");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/Emperor");			item.rare = 9;			item.width = dims.Width;			item.value = 250000;			item.height = dims.Height;            item.GetGlobalItem<ExxoAvalonOriginsGlobalItemInstance>().tome = true;        }        public override void UpdateAccessory(Player player, bool hideVisual)        {            player.magicDamage += 0.25f;            player.minionDamage += 0.25f;            player.meleeDamage += 0.25f;            player.rangedDamage += 0.25f;            player.thrownDamage += 0.25f;            player.meleeCrit += 12;            player.magicCrit += 12;            player.rangedCrit += 12;            player.thrownCrit += 12;            player.manaCost -= 0.2f;            player.statDefense += 14;            player.statLifeMax2 += 100;            player.statManaMax2 += 200;        }    }}