using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using ExxoAvalonOrigins.Projectiles;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class EnergizedWeaponStaff : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Energized Weapon Staff");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/EnergizedWeaponStaff");			item.summon = true;			item.damage = 62;			item.shootSpeed = 14f;			item.buffType = ModContent.BuffType<Buffs.Miniweapon>();			item.mana = 30;			item.noMelee = true;			item.rare = 9;			item.width = dims.Width;			item.useTime = 30;			item.knockBack = 2.5f;			item.shoot = ModContent.ProjectileType<Miniaxe>();			item.useStyle = 1;			item.value = Item.sellPrice(0, 30, 0, 0);			item.useAnimation = 30;			item.height = dims.Height;		}		public override bool Shoot(Player player, ref Vector2 position, ref float speedX, ref float speedY, ref int type, ref int damage,			ref float knockBack)		{			Projectile.NewProjectile(player.Center.X - 40f, player.Center.Y - 40f, 0f, 0f, ModContent.ProjectileType<Miniaxe>(), damage, knockBack, player.whoAmI, 0f, 0f);			Projectile.NewProjectile(player.Center.X + 40f, player.Center.Y - 40f, 0f, 0f, ModContent.ProjectileType<Minibur>(), damage, knockBack, player.whoAmI, 0f, 0f);			Projectile.NewProjectile(player.Center.X - 40f, player.Center.Y + 40f, 0f, 0f, ModContent.ProjectileType<Minihammer>(), damage, knockBack, player.whoAmI, 0f, 0f);			return false;		}	}}