using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class EssenceofBlight : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Essence of Blight");			Tooltip.SetDefault("Summons Blight");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/EssenceofBlight");			item.consumable = true;			item.rare = 8;			item.width = dims.Width;			item.useTime = 45;			item.useStyle = 4;			item.maxStack = 20;			item.useAnimation = 45;			item.height = dims.Height;        }        public override bool CanUseItem(Player player)        {            if (NPC.AnyNPCs(ModContent.NPCType<NPCs.Blight>())) return false;            return true;        }        public override bool UseItem(Player player)        {            NPC.SpawnOnPlayer(player.whoAmI, ModContent.NPCType<NPCs.Blight>());            return true;        }    }}