using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class EternitysMoon : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Eternity's Moon");			Tooltip.SetDefault("+20 mana, -5% mana cost");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/EternitysMoon");			item.rare = 2;			item.width = dims.Width;			item.value = 15000;			item.height = dims.Height;            item.GetGlobalItem<ExxoAvalonOriginsGlobalItemInstance>().tome = true;        }        public override void UpdateAccessory(Player player, bool hideVisual)        {            player.statManaMax2 += 20;            player.manaCost -= 0.05f;        }    }}