using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class GoldApple : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Golden Apple");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/GoldApple");			item.width = dims.Width;			item.height = dims.Height;		}	}}