using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class GoldminePickaxe : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Goldmine Pickaxe");			Tooltip.SetDefault("Able to mine Dungeon Brick");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/GoldminePickaxe");			item.damage = 9;			item.autoReuse = true;			item.useTurn = true;			item.scale = 1f;			item.pick = 62;			item.rare = 1;			item.width = dims.Width;			item.useTime = 20;			item.knockBack = 0.5f;			item.melee = true;			item.useStyle = 1;			item.value = Item.sellPrice(0, 0, 36, 0);			item.useAnimation = 20;			item.height = dims.Height;		}	}}