using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class Hellrazer : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Hellrazer");			Tooltip.SetDefault("Fires a powerful, high velocity bullet\nMost bullets turn into Explosive rounds");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/Hellrazer");			item.damage = 100;			item.autoReuse = true;			item.useTurn = false;			item.useAmmo = AmmoID.Bullet;			item.shootSpeed = 8f;			item.crit += 10;			item.ranged = true;			item.rare = 11;			item.noMelee = true;			item.width = dims.Width;			item.knockBack = 12f;			item.useTime = 30;			item.shoot = 14;			item.value = Item.sellPrice(0, 30, 0, 0);			item.useStyle = 5;			item.useAnimation = 30;			item.height = dims.Height;		}		public override bool Shoot(Player player, ref Vector2 position, ref float speedX, ref float speedY, ref int type, ref int damage, ref float knockBack)		{			int shotProjectile =				Projectile.NewProjectile(position.X, position.Y, speedX, speedY, type, damage, knockBack);			Main.projectile[shotProjectile].GetGlobalProjectile<ExxoAvalonOriginsGlobalProjectileInstance>().lightOnFire =				true;			return false;		}	}}