using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class OblivionGlaive : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Oblivion Glaive");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/OblivionGlaive");			item.damage = 120;			item.noUseGraphic = true;			item.scale = 1f;			item.shootSpeed = 5.6f;			item.rare = 9;			item.noMelee = true;			item.width = dims.Width;			item.useTime = 9;			item.knockBack = 6.5f;			item.shoot = ModContent.ProjectileType<Projectiles.OblivionGlaive>();			item.melee = true;			item.useStyle = 5;			item.value = Item.sellPrice(0, 20, 0, 0);			item.useAnimation = 9;			item.height = dims.Height;		}	}}