using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class OnyxStaff : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Onyx Staff");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/OnyxStaff");			item.magic = true;			item.damage = 116;			item.autoReuse = true;			item.shootSpeed = 9.5f;			item.mana = 20;			item.rare = 9;			item.noMelee = true;			item.width = dims.Width;			item.useTime = 19;			item.knockBack = 25.5f;			item.shoot = ModContent.ProjectileType<Projectiles.OnyxBolt>();			item.useStyle = 5;			item.value = 350000;			item.useAnimation = 19;			item.height = dims.Height;		}	}}