using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class OpalStaff : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Opal Staff");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/OpalStaff");			item.magic = true;			item.damage = 90;			item.autoReuse = true;			item.shootSpeed = 9.5f;			item.mana = 14;			item.rare = 8;			item.noMelee = true;			item.width = dims.Width;			item.useTime = 23;			item.knockBack = 7.5f;			item.shoot = ModContent.ProjectileType<Projectiles.OpalBolt>();			item.useStyle = 5;			item.value = 300000;			item.useAnimation = 23;			item.height = dims.Height;		}		public override bool Shoot(Player player, ref Vector2 position, ref float speedX, ref float speedY, ref int type, ref int damage,			ref float knockBack)		{			for (int num182 = 0; num182 < 3; num182++)			{				float num183 = speedX;				float num184 = speedY;				num183 += (float)Main.rand.Next(-30, 31) * 0.05f;				num184 += (float)Main.rand.Next(-30, 31) * 0.05f;				Projectile.NewProjectile(position.X, position.Y, num183, num184, type, damage, knockBack, player.whoAmI, 0f, 0f);			}			return false;		}	}}