using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class PeridotStaff : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Peridot Staff");		}		public override void SetDefaults()		{			item.CloneDefaults(ItemID.SapphireStaff);
			Item.staff[item.type] = true;			Rectangle dims = ExxoAvalonOrigins.getDims("Items/PeridotStaff");			item.magic = true;			item.damage = 30;			item.autoReuse = true;			item.shootSpeed = 7.75f;			item.mana = 10;			item.rare = 1;			item.noMelee = true;			item.width = dims.Width;			item.useTime = 26;			item.knockBack = 4.75f;			item.shoot = ModContent.ProjectileType<Projectiles.PeridotBolt>();			item.useStyle = 5;			item.value = 36000;			item.useAnimation = 26;			item.height = dims.Height;            item.UseSound = SoundID.Item43;
        }	}}