using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class PyroscoricLongsword : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Pyroscoric Longsword");			Tooltip.SetDefault("'It burns, I tell you!'");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/PyroscoricLongsword");			item.damage = 97;			item.autoReuse = false;			item.useTurn = true;			item.scale = 1f;			item.rare = 8;			item.width = dims.Width;			item.useTime = 25;			item.knockBack = 7f;			item.melee = true;			item.useStyle = 1;			item.value = Item.sellPrice(0, 7, 63, 0);			item.useAnimation = 25;			item.height = dims.Height;		}	}}