using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class QuadSunfury : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Quad Sunfury");			Tooltip.SetDefault("Press the N key to change modes");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/QuadSunfury");			item.damage = 100;			item.noUseGraphic = true;			item.channel = true;			item.scale = 1.1f;			item.shootSpeed = 12f;			item.noMelee = true;			item.rare = 8;			item.width = dims.Width;			item.useTime = 61;			item.knockBack = 8f;			item.shoot = 0;			item.melee = true;			item.value = 999000;			item.useStyle = 5;			item.useAnimation = 61;			item.height = dims.Height;		}	}}