using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class Rainbringer : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Rainbringer");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/Rainbringer");			item.rare = 4;			item.width = dims.Width;			item.useTime = 30;			item.shoot = ModContent.ProjectileType<Projectiles.Rainbringer>();			item.useStyle = 1;			item.value = Item.sellPrice(0, 2, 70, 0);			item.useAnimation = 15;			item.height = dims.Height;		}	}}