using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class RottenCrate : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Corrupt Crate");			Tooltip.SetDefault("Right click to open");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/RottenCrate");			item.rare = 1;			item.width = dims.Width;			item.maxStack = 99;			item.value = Item.sellPrice(0, 0, 10, 0);			item.height = dims.Height;		}	}}