using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class ShadowPotion : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Shadow Potion");			Tooltip.SetDefault("Enables teleportation to the cursor");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/ShadowPotion");			item.buffType = ModContent.BuffType<Buffs.Shadows>();			item.consumable = true;			item.rare = 2;			item.width = dims.Width;			item.useTime = 15;			item.useStyle = 2;			item.maxStack = 100;			item.useAnimation = 15;			item.height = dims.Height;			item.buffTime = 25200;		}	}}