using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class Shurikerang : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Shurikerang");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/Shurikerang");			item.damage = 18;			item.noUseGraphic = true;			item.scale = 1.2f;			item.maxStack = 10;			item.shootSpeed = 12f;			item.crit += 3;			item.ranged = true;			item.noMelee = true;			item.rare = 3;			item.width = dims.Width;			item.useTime = 12;			item.knockBack = 3f;			item.shoot = ModContent.ProjectileType<Projectiles.Shurikerang>();			item.useStyle = 1;			item.value = 30000;			item.useAnimation = 12;			item.height = dims.Height;		}	}}