using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class SlimeTalisman : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Slime Talisman");			Tooltip.SetDefault("Provides immunity to slimes");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/SlimeTalisman");			item.rare = 3;			item.width = dims.Width;			item.value = Item.sellPrice(0, 1, 0, 0);			item.accessory = true;			item.height = dims.Height;		}        public override void UpdateAccessory(Player player, bool hideVisual)        {            player.GetModPlayer<ExxoAvalonOriginsModPlayer>().slimeImmune = true;        }    }}