using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class SlimeTorch : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Slime Torch");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/SlimeTorch");			item.autoReuse = true;			item.noWet = true;			item.useTurn = true;			item.maxStack = 999;			item.holdStyle = 1;			item.consumable = true;			item.createTile = ModContent.TileType<Tiles.Torches>();			item.placeStyle = 2;			item.width = dims.Width;			item.useTime = 10;			item.flame = true;			item.useStyle = 1;			item.value = Item.sellPrice(0, 0, 0, 15);			item.useAnimation = 15;			item.height = dims.Height;			item.notAmmo = true;		}	}}