using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class SonicScrewdriverMkII : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Sonic Screwdriver Mk II");			Tooltip.SetDefault("Reveals treasures, ores, and mobs\nTells time and shows position");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/SonicScrewdriverMkII");			item.rare = 6;			item.width = dims.Width;			item.useTime = 70;			item.value = Item.sellPrice(0, 4, 0, 0);			item.useStyle = 3;			item.useAnimation = 70;			item.height = dims.Height;		}	}}