using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;using Terraria.DataStructures;namespace ExxoAvalonOrigins.Items{	class SoulofIce : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Soul of Ice");			Tooltip.SetDefault("'The essence of ice creatures'");			Main.RegisterItemAnimation(item.type, new DrawAnimationVertical(5, 4));			ItemID.Sets.AnimatesAsSoul[item.type] = true;			ItemID.Sets.ItemIconPulse[item.type] = true;			ItemID.Sets.ItemNoGravity[item.type] = true;		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/SoulofIce");			item.autoReuse = true;			item.consumable = true;			item.createTile = ModContent.TileType<Tiles.IcesoulBlock>();			item.rare = 4;			item.width = dims.Width;			item.useTurn = true;			item.useTime = 10;			item.useStyle = 1;			item.maxStack = 999;			item.value = 50000;			item.useAnimation = 15;			item.height = dims.Height;		}	}}