using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;using Terraria.DataStructures;namespace ExxoAvalonOrigins.Items{	class SoulofTorture : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Soul of Torture");			Tooltip.SetDefault("'The essence of cruelty'\nIncreases critical strike chance if placed");			Main.RegisterItemAnimation(item.type, new DrawAnimationVertical(5, 4));			ItemID.Sets.AnimatesAsSoul[item.type] = true;			ItemID.Sets.ItemIconPulse[item.type] = true;			ItemID.Sets.ItemNoGravity[item.type] = true;		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/SoulofTorture");			item.autoReuse = true;			item.consumable = true;			item.createTile = ModContent.TileType<Tiles.TorturesoulBlock>();			item.rare = 10;			item.width = dims.Width;			item.useTurn = true;			item.useTime = 10;			item.useStyle = 1;			item.maxStack = 999;			item.value = 90000;			item.useAnimation = 15;			item.height = dims.Height;		}	}}