using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class SoulofWight : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Soul of Wight");			Tooltip.SetDefault("'The essence of ghosts'");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/SoulofWight");			item.autoReuse = true;			item.consumable = true;			item.createTile = ModContent.TileType<Tiles.WightsoulBlock>();			item.rare = 9;			item.width = dims.Width;			item.useTurn = true;			item.useTime = 10;			item.useStyle = 1;			item.maxStack = 999;			item.value = 70000;			item.useAnimation = 15;			item.height = dims.Height;		}	}}