using Microsoft.Xna.Framework;
using Microsoft.Xna.Framework.Graphics;
using System.Collections.Generic;
using Terraria;
using Terraria.DataStructures;
using Terraria.ID;
using Terraria.ModLoader;namespace ExxoAvalonOrigins.Items{	[AutoloadEquip(EquipType.Head)]	class SpectrumHelmet : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Spectrum Helmet");			Tooltip.SetDefault("20% increased ranged damage\n3% increased ranged critical strike chance");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/SpectrumHelmet");			item.defense = 32;			item.rare = 10;			item.width = dims.Width;			item.value = Item.sellPrice(0, 40, 0, 0);			item.height = dims.Height;		}		public override bool IsArmorSet(Item head, Item body, Item legs)		{			return body.type == ModContent.ItemType<SpectrumBreastplate>() && legs.type == ModContent.ItemType<SpectrumGreaves>();		}		public override void UpdateArmorSet(Player player)		{			player.setBonus = "Ranged projectiles have a chance to split in two";			player.GetModPlayer<ExxoAvalonOriginsModPlayer>().splitProj = true;		}		public override void UpdateEquip(Player player)		{			player.rangedDamage += 0.25f;			player.rangedCrit += 3;		}	}}