using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class StaminaCrystal : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Stamina Crystal");			Tooltip.SetDefault("Permanently increases maximum stamina by 20");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/StaminaCrystal");			item.consumable = true;			item.rare = 3;			item.width = dims.Width;			item.useTime = 30;			item.maxStack = 999;			item.useStyle = 4;			item.value = 95000;			item.useAnimation = 30;			item.height = dims.Height;		}	}}