using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class Staminastone : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Staminastone");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/Staminastone");			item.autoReuse = true;			item.consumable = true;			item.createTile = ModContent.TileType<Tiles.Staminastone>();			item.rare = 3;			item.width = dims.Width;			item.useTime = 10;			item.useTurn = true;			item.useStyle = 1;			item.maxStack = 999;			item.value = Item.sellPrice(0, 0, 2, 50);			item.useAnimation = 15;			item.height = dims.Height;		}		public override bool CanUseItem(Player player)		{			return player.GetModPlayer<ExxoAvalonOriginsModPlayer>().statStam <= 300;		}		public override bool UseItem(Player player)		{			player.GetModPlayer<ExxoAvalonOriginsModPlayer>().statStamMax += 20;			player.GetModPlayer<ExxoAvalonOriginsModPlayer>().statStam += 20;			player.GetModPlayer<ExxoAvalonOriginsModPlayer>().statStamMax2 += 20;			return true;		}	}}