using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class StickyCharm : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Sticky Charm");			Tooltip.SetDefault("Reduces damage taken by 10% and negates fall damage\nProvides immunity to slimes");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/StickyCharm");			item.rare = 3;			item.width = dims.Width;			item.accessory = true;			item.value = 200000;			item.height = dims.Height;		}		public override void UpdateAccessory(Player player, bool hideVisual)		{			player.endurance += 0.1f;			player.noFallDmg = true;		}	}}