using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class StickyDynamite : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Sticky Dynamite");			Tooltip.SetDefault("A large explosion that will destroy most tiles\n'Tossing may be difficult.'\nLEGACY ITEM");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/StickyDynamite");			item.noUseGraphic = true;			item.maxStack = 100;			item.shootSpeed = 4f;			item.consumable = true;			item.noMelee = true;			item.rare = 2;			item.width = dims.Width;			item.useTime = 40;			item.shoot = ModContent.ProjectileType<Projectiles.StickyDynamite>();			item.useStyle = 1;			item.value = Item.sellPrice(0, 0, 35, 0);			item.useAnimation = 40;			item.height = dims.Height;		}	}}