using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class SummmonerEmblem : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Summoner Emblem");			Tooltip.SetDefault("15% increased minion damage");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/SummmonerEmblem");			item.rare = 4;			item.width = dims.Width;			item.accessory = true;			item.value = 100000;			item.height = dims.Height;		}		public override void UpdateAccessory(Player player, bool hideVisual)		{			player.minionDamage += 0.15f;		}	}}