using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class SuperhardmodeBar : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Superhardmode Bar");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/SuperhardmodeBar");			item.rare = 8;			item.width = dims.Width;			item.maxStack = 999;			item.value = Item.sellPrice(0, 1, 30, 0);			item.height = dims.Height;		}	}}