using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TaleoftheRedLotus : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Tale of the Red Lotus");			Tooltip.SetDefault("Tome\n+5% ranged damage\n+20 HP");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TaleoftheRedLotus");			item.rare = 4;			item.width = dims.Width;			item.value = 5000;			item.height = dims.Height;            item.GetGlobalItem<ExxoAvalonOriginsGlobalItemInstance>().tome = true;        }        public override void UpdateAccessory(Player player, bool hideVisual)        {            player.rangedDamage += 0.05f;            player.statLifeMax2 += 20;        }    }}