using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class Terraspin : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Terraspin");			Tooltip.SetDefault("Fires a spread of typhoons");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/Terraspin");			item.magic = true;			item.damage = 98;			item.autoReuse = true;			item.channel = true;			item.useTurn = true;			item.shootSpeed = 9f;			item.crit += 9;			item.mana = 20;			item.noMelee = true;			item.rare = 9;			item.width = dims.Width;			item.knockBack = 7f;			item.useTime = 25;			item.shoot = ModContent.ProjectileType<Projectiles.TerraTyphoon>();			item.useStyle = 5;			item.value = Item.sellPrice(0, 50, 0, 0);			item.useAnimation = 25;			item.height = dims.Height;		}		public override bool Shoot(Player player, ref Vector2 position, ref float speedX, ref float speedY, ref int type, ref int damage,			ref float knockBack)		{			for (int num212 = 0; num212 < 3; num212++)			{				float num213 = speedX;				float num214 = speedY;				num213 += (float)Main.rand.Next(-30, 31) * 0.05f;				num214 += (float)Main.rand.Next(-30, 31) * 0.05f;				Projectile.NewProjectile(position.X, position.Y, num213, num214, type, damage, knockBack, player.whoAmI, 0f, 0f);			}			return false;		}	}}