using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TheOasisRemembered : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("The Oasis Remembered");			Tooltip.SetDefault("Tome\n20% increased minion damage and knockback");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TheOasisRemembered");			item.rare = ItemRarityID.Yellow;			item.width = dims.Width;			item.value = Item.sellPrice(0, 0, 40);
            item.height = dims.Height;            item.GetGlobalItem<ExxoAvalonOriginsGlobalItemInstance>().tome = true;		}        public override void UpdateAccessory(Player player, bool hideVisual)        {            player.minionDamage += 0.2f;            player.minionKB += 0.2f;        }    }}