using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class ThePlumHarvest : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("The Plum Harvest");			Tooltip.SetDefault("Tome\n30% chance to not consume ammo");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/ThePlumHarvest");			item.rare = ItemRarityID.Yellow;			item.width = dims.Width;			item.value = 150000;			item.height = dims.Height;            item.GetGlobalItem<ExxoAvalonOriginsGlobalItemInstance>().tome = true;        }        //Update Accs not needed - ammo done in ModPlayer    }}