using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TheVoidlands : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("The Voidlands");			Tooltip.SetDefault("Tome\n+15% damage, +3% critical strike chance\n+60 HP, +40 mana");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TheVoidlands");			item.rare = 6;			item.width = dims.Width;			item.value = 105000;			item.height = dims.Height;            item.GetGlobalItem<ExxoAvalonOriginsGlobalItemInstance>().tome = true;		}        public override void UpdateAccessory(Player player, bool hideVisual)        {            player.magicDamage += 0.15f;            player.minionDamage += 0.15f;            player.meleeDamage += 0.15f;            player.rangedDamage += 0.15f;            player.thrownDamage += 0.15f;            player.meleeCrit += 3;            player.magicCrit += 3;            player.rangedCrit += 3;            player.thrownCrit += 3;            player.statLifeMax2 += 60;            player.statManaMax2 += 40;        }    }}