using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class Timechanger : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Timechanger");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/Timechanger");			item.rare = 4;			item.width = dims.Width;			item.useTime = 30;			item.shoot = ModContent.ProjectileType<Projectiles.Timechanger>();			item.useStyle = 1;			item.value = Item.sellPrice(0, 2, 70, 0);			item.useAnimation = 15;			item.height = dims.Height;		}

        public override void AddRecipes()
        {
			ModRecipe recipe = new ModRecipe(mod);
			recipe.AddRecipeGroup(RecipeGroup.recipeGroupIDs["ExxoAvalonOrigins:GoldBar"], 30);			recipe.AddIngredient(ItemID.SoulofLight, 15);			recipe.AddIngredient(ItemID.SoulofNight, 15);			recipe.AddRecipeGroup(RecipeGroup.recipeGroupIDs["ExxoAvalonOrigins:Tier3Watch"], 1);			recipe.AddTile(TileID.MythrilAnvil);			recipe.SetResult(item.type);			recipe.AddRecipe();
		}	}}