using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TimechangerMkII : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Timechanger Mk II");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TimechangerMkII");			item.rare = 6;			item.width = dims.Width;			item.useTime = 30;			item.shoot = ModContent.ProjectileType<Projectiles.TimechangerMkII>();			item.useStyle = 1;			item.value = Item.sellPrice(0, 3, 70, 0);			item.useAnimation = 15;			item.height = dims.Height;		}	}}