using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TomorrowsPhoenix : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Tomorrow's Phoenix");			Tooltip.SetDefault("Tome\n8% increased minion damage\n5% increased minion knockback");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TomorrowsPhoenix");			item.rare = 3;			item.width = dims.Width;			item.value = Item.sellPrice(0, 0, 10);
            item.height = dims.Height;            item.GetGlobalItem<ExxoAvalonOriginsGlobalItemInstance>().tome = true;		}        public override void UpdateAccessory(Player player, bool hideVisual)        {            player.minionDamage += 0.08f;            player.minionKB += 0.05f;        }    }}