using Microsoft.Xna.Framework;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using Terraria;using Terraria.ModLoader;using Terraria.ID;namespace ExxoAvalonOrigins.Items{	class TourmalineStaff : ModItem	{		public override void SetStaticDefaults()		{			DisplayName.SetDefault("Tourmaline Staff");		}		public override void SetDefaults()		{			Rectangle dims = ExxoAvalonOrigins.getDims("Items/TourmalineStaff");			item.magic = true;			item.damage = 28;			item.autoReuse = true;			item.shootSpeed = 7.5f;			item.mana = 9;			item.rare = 1;			item.noMelee = true;			item.width = dims.Width;			item.useTime = 28;			item.knockBack = 4.5f;			item.shoot = ModContent.ProjectileType<Projectiles.TourmalineBolt>();			item.useStyle = 5;			item.value = 35000;			item.useAnimation = 28;			item.height = dims.Height;		}	}}